        apFlod.attr('flod', false);
        this.apFlod = apFlod;
    }
    addWaves(){
        const waves = $(`
            <div id='waves'>
                <svg class="waves" xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink" viewBox="0 24 150 28" preserveAspectRatio="none" shape-rendering="auto">
                    <defs>
