Page._content:
.clock {
    position: relative;
    width: 680px;
    height: 680px;
    border-radius: 50%;
    font-size: 12px;
    overflow: hidden;
    line-height: 20px;
    user-select: none;
    backdrop-filter: blur(5px);
    border: 1px solid rgba(255, 255, 255, .5);
    color: #fafafa;
    font-weight: 700;
    z-index: 2;
    list-style: none;
}

.clock .date {
    position: absolute;
    z-index: 1;
    width: 50%;
    height: 20px;
    text-align: center;
    top: 49%;
    left: 26%;
}

.cl-clock-bg {
    position: absolute;
    top: 10px;
    left: 10px;
    right: 10px;
    bottom: 10px;
    border-radius: 50%;
    background-color: #ff0049;
    background: radial-gradient(transparent, rgba(255, 255, 255, .2));
    backdrop-filter: blur(25px);
    border: 1px solid rgba(255, 255, 255, .5);
    border-bottom: none;
    border-right: none;
    box-shadow: -10px -10px 20px rgba(255, 255, 255, .1),
        10px 10px 20px rgba(0, 0, 0, .1),
        0px 40px 50px rgba(0, 0, 0, .2);
}

.clock .hour {
    position: absolute;
    z-index: 3;
    width: 42%;
    height: 20px;
    top: 49%;
    left: 29%;
    transition: transform 0.3s ease-in-out 0s;
    transform: rotate(0deg);
}

.clock .hour>div {
    position: absolute;
    width: 100%;
    height: 100%;
    right: 0;
    top: 0;
    transition: transform 1s ease-in-out 0s;
    transform: rotate(0deg);
}

.clock .hour>div>div {
    float: right;
    width: 60px;
    text-align: right;
}

.clock .minute {
    position: absolute;
    z-index: 4;
    width: 68%;
    height: 20px;
    top: 49%;
    left: 16%;
}

.clock .sec {
    z-index: 5;
    width: 92%;
    height: 20px;
    top: 49%;
    left: 4%;
}

.clock>hr {
    width: 0;
    height: 0;
    border: none;
    transition: width .3s ease-in-out 0s;
    position: absolute;
    background-color: #fff;

    overflow: visible;
}

.clock>hr.active {
    width: 46%;
    height: 1px;
    top: 46%;
    left: 50%;
}

.clock>hr.active:before {
    content: '';
    position: absolute;
    width: 10px;
    height: 10px;
    transform: translate(-50%, -30%);
    background: #ff0;
    border-radius: 50%;
    z-index: 60;
    top: -1px;
    left: 0;
}

