                actionTextColor: '#fff',
                duration: 500000,
                pos: snackbarPos,
                actionTextColor: '#F8A061',
            })
            // alert('点击左下角按钮可再次开启音乐播放器')
            setTimeout(() => {
                this.apFlod.attr('flod', true);
            }, 3000)

            if (positionObj.left) {
                // alert('靠左边');
                this.apFlod.attr('left', true);
            } else {
                // alert('靠右边');
                this.apFlod.attr('left', false);
            }
        });
        this.apFlod.bind('click', (e) => {
            this.apFlod.attr('flod', false);
