            // this.aplayerContainer.css({"left": 0, "width":"14rem"});
        })
    }
    bindAplayerMove() {
        $('.aplayer-title').bind('mousedown', (e) => {
            // 算出鼠标相对元素的位置
            const disX = e.clientX - this.aplayerContainer[0].offsetLeft;
